package JavaBasic;

public class ForAdvance {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5;
		int b=5;
		
		for (a=0;a<5;a++) {
			
			
			for(b=5;b>a;b--)
				
			{
				
				System.out.print(" ");
				
			}
			
			System.out.println("*");
			
			
			
			
		}

	}

}
