package JavaBasic;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1;
		
		System.out.println("for output"+'\n');
		
		for(int b=1;b<=10;b++) {
			
			System.out.println(+b);
		}
		
		System.out.println("do wile output"+'\n');
		do {
			
		System.out.println(+a);
		a++;
		}
		
		while(a<=10);
			

	}

}
