package JavaBasic;

public class JavaForNested {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		int b;
		
		for(a=1;a<=5;a++)
		
		{
			
		  for(b=5;b>=a;b--) 
		    {
			  
			  System.out.print("*");
			  
		     }
		  
		  System.out.println("");
		  
		  
					
		}
		

	}

}
