package JavaBasic;

public class LargestNumberWithoutIF {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=20;
		int b=30;
		
		int large=a>b?a:b;// if true then a will print and if false b will print
		
		System.out.println(large);

	}

}
