package JavaBasic;

public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		int b;
		
		
		for(a=0;a<=5;a++) {
			
			System.out.println("   "); 
			
			for(b=0;b<a;b++){
				
				System.out.print(b);
				
			}//end on Inner For
				
			
		}//end of upper for
		
		
		

	}
	

}
