package JavaBasic;

public class StarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
         int a=0;

		
		for(a=0;a<5;a++)//rows will be 5
			
		{
			
			for(int b=5;b>a;b--)//coulmn will be 5 to 1
			{
				
			System.out.print("*");
			
			
			}
			
			System.out.println();
		}
		
		
		
	for(int c=0;c<5;c++)//rows will be 5
			
		{
			
			for(int d=0;d<=c;d++)//coulmn will be 1 to 5
			{
				
			System.out.print("*");
			
			
			}
			
			System.out.println();
		}
	
	

	

}
	
}

