package JavaBasic;

public class StarPattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int a=0;a<=5;a++) {
			
			
			for(int b=0;b<=a;b++)
			{
				
				System.out.print("*");
				
			}
			
			
			System.out.println();
		}
		
		
		

		for(int c=0;c<=5;c++) {
			
			
			for(int d=5;d>=c;d--)
			{
				
				System.out.print("*");
				
			}
			
			
			System.out.println();
		}
		
		

	}

}
