package JavaBasic;

public class StarPattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int a=0;a<=5;a++)
			
		{

			
		     for(int b=5;b>=a;b--) {
		    	 
		     
		    	 System.out.print(" ");
				
			
		     }
		     
		     
		     for(int c=0;c<=a;c++)
		    	 
		     {
		    	 System.out.print("*"); 
		    	 
		     }
		    
		     
		     
		     System.out.println();
			
			
		}

	}

}
