package JavaBasic;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1;
		
		//find even numbers from 1 to 10;
		
		while(a<=10) {
			
			if(a%2==0)
			{
			
			System.out.println(+a);
			
			}
			
			
			a++;
			
		}

	}

}
